function removeActiveClass(){document.querySelectorAll(".js-menu-item").forEach(e=>e.classList.remove("active"))}function addActiveClass(e){const t=document.querySelector(`.js-menu-item[href="#${e}"]`);t&&t.classList.add("active")}function onScroll(){const e=document.querySelectorAll("section");let t="";e.forEach(e=>{const n=e.offsetTop,c=e.clientHeight;pageYOffset>=n-c/3&&(t=e.getAttribute("id"))}),t&&(removeActiveClass(),addActiveClass(t))}function clickMenuHandler(e){document.querySelectorAll(".js-menu-item").forEach(e=>{e.classList.remove("active")}),e.classList.add("active")}function menuOpen(){const e=document.querySelector(".js-menu-mobile");document.querySelector(".js-menu-close");e.classList.add("open")}function menuClose(){const e=document.querySelector(".js-menu-mobile");document.querySelector(".js-menu-open");e.classList.remove("open")}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".js-menu-open"),t=document.querySelector(".js-menu-close");e&&e.addEventListener("click",menuOpen),t&&t.addEventListener("click",menuClose),document.addEventListener("mouseup",function(e){const t=document.querySelector(".js-menu-mobile .burger-menu__list");document.querySelector(".js-menu-close"),document.querySelector(".js-menu-open");t&&!t.contains(e.target)&&t.parentElement.classList.remove("open")});document.querySelectorAll(".js-menu-item").forEach(e=>{e.addEventListener("click",e=>clickMenuHandler(e))}),window.addEventListener("scroll",onScroll)});